import React, { useState, useEffect } from 'react';
import { 
  TextField, 
  Paper, 
  Typography, 
  Box, 
  Container,
  Chip,
  IconButton,
  Tooltip,
  Button
} from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import InfoIcon from '@mui/icons-material/Info';
import { getAllTechnologies, validateTechnology } from '../data/technologies';

const TechnologyValidator = () => {
  const [inputValue, setInputValue] = useState('');
  const [isValid, setIsValid] = useState(null);
  const [options] = useState(getAllTechnologies());

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
    // Reset de la validación al cambiar el input
    setIsValid(null);
  };

  const handleValidate = () => {
    if (inputValue) {
      setIsValid(validateTechnology(inputValue));
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 4, 
          borderRadius: 2,
          backgroundColor: '#ffffff'
        }}
      >
        <Typography 
          variant="h4" 
          component="h1" 
          gutterBottom
          sx={{ 
            color: '#333',
            fontWeight: 600,
            textAlign: 'center',
            mb: 3
          }}
        >
          Diccionario de Tecnologías y Frameworks
        </Typography>

        <Box sx={{ mb: 3 }}>
          <Typography 
            variant="body2" 
            color="textSecondary"
            sx={{ mb: 1, textAlign: 'center' }}
          >
            Busca y valida si una tecnología está en nuestro diccionario de 125 tecnologías y 300 frameworks
          </Typography>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          gap: 1,
          justifyContent: 'center'
        }}>
          <TextField 
            label="Tecnología o Framework" 
            variant="outlined"
            value={inputValue}
            onChange={handleInputChange}
            sx={{ 
              maxWidth: '400px',
              '& .MuiOutlinedInput-root': {
                borderRadius: 1.5,
                backgroundColor: '#f9f9f9',
              }
            }}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleValidate();
              }
            }}
          />

          <Tooltip title="Validar">
            <Button 
              variant="contained"
              onClick={handleValidate}
              sx={{ 
                bgcolor: '#3f51b5', 
                '&:hover': { bgcolor: '#303f9f' },
                height: '56px'
              }}
            >
              Validar
            </Button>
          </Tooltip>
        </Box>

        {isValid !== null && (
          <Box 
            sx={{ 
              mt: 3, 
              p: 2, 
              borderRadius: 1.5, 
              bgcolor: isValid ? 'rgba(46, 125, 50, 0.08)' : 'rgba(211, 47, 47, 0.08)',
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              maxWidth: '600px',
              mx: 'auto'
            }}
          >
            {isValid ? (
              <>
                <CheckCircleIcon color="success" />
                <Typography variant="body1" color="success.main">
                  ¡La tecnología está registrada en nuestro diccionario!
                </Typography>
                <Chip 
                  label="Válida" 
                  color="success" 
                  size="small" 
                  sx={{ ml: 'auto' }}
                />
              </>
            ) : (
              <>
                <CancelIcon color="error" />
                <Typography variant="body1" color="error">
                  Esta tecnología no está en nuestro listado de 125 tecnologías y 300 frameworks.
                </Typography>
                <Chip 
                  label="No reconocida" 
                  color="error" 
                  size="small" 
                  sx={{ ml: 'auto' }}
                />
              </>
            )}
          </Box>
        )}
      </Paper>
    </Container>
  );
};

export default TechnologyValidator;
